function powerUpAudioFX(){powerUpAudio=game.add.audio("powerUpFX"),powerUpAudio.allowMultiple=!1,powerUpAudio.play()}function powerDownAudioFX(){powerDownAudio=game.add.audio("powerDownFX"),powerDownAudio.allowMultiple=!1,powerDownAudio.play()}function winAudioFX(){winSceneAudio=game.add.audio("winScene"),winSceneAudio.volume=1.8,winSceneAudio.play()}function loseAudioFX(){loseSceneAudio=game.add.audio("loseScene"),loseSceneAudio.allowMultiple=!1,loseSceneAudio.volume=1.8,loseSceneAudio.play()}function cupMoveAudioFX(){cupOnMoveAudio=game.add.audio("cupOnMove"),cupOnMoveAudio.allowMultiple=!1,cupOnMoveAudio.play()}